//Given an index, return the pokemon
const num = (max, min) =>{
  return Math.floor(Math.random()*(max-min)+min);
};
const randPokemon = (index) =>{
  return pokemonArray[index];
};
const randTeam= () => {
  const teamSize = 6;
  let team = [];
  for (let index = 0; index < teamSize; index++){
    let randomIndex = num(0, pokemonArray.length-1);
    team[index] = pokemonArray[randomIndex];
    //let pokemon = randPokemon(randomIndex);
    //team.push(pokemon);
  };
  return team;
};
